import React, { useState, Component } from "react";
import { useNavigate } from "react-router-dom";
import mystore from "./store";
import C from "./Images/01.jpg";

function Adminlogin() {
  let navigate = useNavigate();

  const [msg, setMsg] = useState("");
  const [customer, setCustomer] = useState({
    email_id: "",
    password: "",
  });

  const handleInput = (ev) => {
    setCustomer((customer) => ({
      ...customer,
      [ev.target.name]: ev.target.value,
    }));
  };

  const submitForm = (ev) => {
    console.log(customer.email_id);
    console.log(customer.password);

    ev.preventDefault();
    const reqOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },

      body: JSON.stringify({
        user_email_id: customer.email_id,
        user_password: customer.password,
      }),
    };

    if (
      customer.email_id === "admin@gmail.com" &&
      customer.password === "admin@123"
    ) {
      mystore.dispatch({ type: "LOGGEDIN" });
      alert("admin logged in successfully");
      navigate("/AdminHome");
    } else {
      alert("invalid credintials");
    }
  };

  return (
    <center>
      <div
        className="container-fluid"
        style={{
          backgroundImage: `url(${C})`,
          height: "1000px",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
        }}
      >
        {<style>{"body{background-color:#DFDFDE}"}</style>}
        <form>
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <h2>ADMIN SIGN IN</h2> <hr />
          <div
            className="form-group"
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <label>
              <b>USER EMAIL :</b>
            </label>{" "}
            &nbsp;&nbsp;&nbsp;
            <input
              type="text"
              style={{ width: "400px" }}
              name="email_id"
              className="form-control"
              placeholder="Enter Email/User ID"
              onChange={handleInput}
              value={customer.email_id}
            />
          </div>
          <div
            className="form-group"
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <label>
              <b>PASSWORD :</b>
            </label>{" "}
            &nbsp; &nbsp;
            <input
              type="password"
              style={{ width: "400px" }}
              name="password"
              className="form-control"
              placeholder="Enter password"
              onChange={handleInput}
              value={customer.password}
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-info" onClick={submitForm}>
              LOGIN
            </button>
          </div>
        </form>
        <h4 style={{ color: "#DFB163" }}></h4>
        <h5>
          {" "}
          <a href="/registerCustomer">Register As Customer </a>{" "}
        </h5>
        <h5>
          <a href="/registermaid">Register As Maid </a>{" "}
        </h5>{" "}
        &nbsp; &nbsp;
      </div>

      <br />
      <h3 style={{ color: "red" }}>
        <i>{msg}</i>
      </h3>
    </center>
  );
}

export default Adminlogin;
